(()=>{"use strict";const e=document.querySelector(".cocktail-list");document.querySelector(".form-cocktail-search").addEventListener("submit",(t=>{var n;t.preventDefault(),e.innerHTML="",n=document.querySelector("#input-cocktail-name").value,fetch(`https://www.thecocktaildb.com/api/json/v1/1/search.php?s=${n}`).then((e=>e.json())).then((t=>{t.drinks.forEach((t=>{const n=`\n      <a class="img-cocktail" \n      style="background-image: linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.3)), url(${t.strDrinkThumb})"\n      href="#">\n      ${t.strDrink}</a>\n      `;e.insertAdjacentHTML("beforeend",n)}))}))}))})();