(()=>{"use strict";fetch("https://www.thecocktaildb.com/api/json/v1/1/search.php?s=martini").then((t=>t.json())).then((t=>{t.drinks.forEach((t=>{const n=`\n      <a class="img-cocktail" \n      style="background-image: linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0.3)), url(${t.strDrinkThumb})"\n      href="#">\n      ${t.strDrink}</a>\n      `;document.querySelector(".cocktail-list").insertAdjacentHTML("beforeend",n)}))}))})();